DROP TABLE IF EXISTS `poi_contents`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `poi_contents` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `facility_id` varchar(255) COLLATE utf8mb4_bin NOT NULL COMMENT 'POI ID<緯度-経度(小数点以下2桁)-ランダム文字列(22桁)の33桁 例：3571-13962-WhB9jk4u8yMmrtbFKgDhBC>',
  `pincode` varchar(255) COLLATE utf8mb4_bin NOT NULL COMMENT 'pincode',
  `brand_id` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'ブランドID',
  `facility_name` varchar(255) COLLATE utf8mb4_bin NOT NULL COMMENT '店舗名<上限100文字>',
  `facility_name_yomi` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '店舗よみがな',
  `facility_introduction` text COLLATE utf8mb4_bin COMMENT '店舗紹介<上限1000文字>',
  `longitude` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '経度',
  `latitude` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '緯度',
  `zipcode` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '郵便番号',
  `region_code` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '都道府県コード<02>',
  `locality` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '市区町村<青森市>',
  `street` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '町名番地<安方2-6-7>',
  `address` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '住所<青森県青森市安方2-6-7>',
  `nearest_station` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '最寄り駅<上限100文字>',
  `access_details` text COLLATE utf8mb4_bin COMMENT '交通詳細(アクセス)',
  `business_hours` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '営業時間',
  `regular_holiday` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '定休日',
  `urls` json DEFAULT NULL COMMENT '公式サイト<["https：//poime-jamprod/test1","https：//poime-jamprod/test2","https：//poime-jamprod/test3"]>',
  `images` json DEFAULT NULL COMMENT '画像URL<["label"："©東日本旅客鉄道株式会社","url"："https：//poime-jamprod-s3-bucket.s3-ap-northeast-1.amazonaws.com/img/35762/sub/35762_sub1.jpg","label"："©東日本旅客鉄道株式会社","url"："https：//poime-jamprod-s3-bucket.s3-ap-northeast-1.amazonaws.com/img/35762/sub/35762_sub2.jpg","https：//poime-jamprod-s3-bucket.s3-ap-northeast-1.amazonaws.com/img/35762/sub/35762_sub3.jpg"]>',
  `phone_numbers` json DEFAULT NULL COMMENT '電話番号<{"label"： "","number"： "017-722-4575"}>',
  `bus_stop` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '最寄りバス停<上限100文字>',
  `category` json DEFAULT NULL COMMENT 'カテゴリ<{"id"： "TH6_tag102","label"： "食べる","treeLevel"： 0}>',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'レコード登録日時',
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'レコード更新日時',
  PRIMARY KEY (`id`),
  UNIQUE KEY `pincode_UNIQUE` (`pincode`),
  KEY `facility_id_IDX` (`facility_id`)
) ENGINE=InnoDB AUTO_INCREMENT=22605 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin COMMENT='POIデータ情報';
