  `convenient_code` int(10) unsigned NOT NULL COMMENT '便利コード',
  `name` varchar(255) COLLATE utf8mb4_bin NOT NULL COMMENT '名称',
  `class_id` int(10) unsigned NOT NULL COMMENT '分類<convenient_types.id>',
  `icon_url` varchar(255) COLLATE utf8mb4_bin NOT NULL COMMENT 'アイコンURL',
  `list_description` varchar(1530) COLLATE utf8mb4_bin NOT NULL COMMENT '一覧用説明',
